/* Update para para actualiza estado meli*/
 UPDATE
    plataforma_ventas_temp pvt
SET
    pvt.status_meli = 0
WHERE
    pvt.fecha = (
        SELECT MAX(fecha)
        FROM plataforma_ventas_temp
        WHERE id_syscom =204626
    )
    AND pvt.id_syscom = 204626



/* Update para para actualiza estado meli con id*/
 UPDATE
    plataforma_ventas_temp pvt
SET
    pvt.status_meli = 0
WHERE
    pvt.fecha = (
        SELECT MAX(id)
        FROM plataforma_ventas_temp
        WHERE id_syscom =176064
    )
    AND pvt.id_syscom = 176064



